+// Cart functionality for Herbert Schildt Book Store
+// Clear old cart
+
+class ShoppingCart {
+    constructor() {
+        this.items = JSON.parse(localStorage.getItem('bookCart')) || []; // load from localStorage
+        this.shippingCost = 5.99;
+        this.taxRate = 0.08; // 8% tax rate
+        this.booksData = []; // will store all-books.json data
+
+        this.init();
+    }
+
+    async init() {
+        await this.loadBooksData();
+        this.renderCart();
+        this.updateCartCount();
+        this.bindEvents();
+    }
+
+    // Load all-books.json
+    async loadBooksData() {
+        try {
+            const response = await fetch('all-books.json');
+            this.booksData = await response.json();
+        } catch (err) {
+            console.error('Error loading all-books.json:', err);
+        }
+    }
+
+    resetTotalsIfEmpty() {
+        if (!this.items || this.items.length === 0) {
+            const subtotalEl = document.getElementById('subtotal');
+            const totalEl = document.getElementById('total');
+            const checkoutBtn = document.getElementById('checkout-btn');
+
+            if (subtotalEl) subtotalEl.textContent = `$0.00`;
+            if (totalEl) totalEl.textContent = `$0.00`;
+            if (checkoutBtn) checkoutBtn.disabled = true;
+
+            console.log('Cart is empty: totals reset to $0.00');
+        }
+    }
+
+    // Save cart to localStorage
