// Cart functionality for Herbert Schildt Book Store
class ShoppingCart {
    constructor() {
        this.items = this.loadCartFromStorage();
        this.shippingCost = 5.99;
        this.taxRate = 0.08; // 8% tax rate
        
        // Sample book data for demonstration
        this.bookData = {
            'java-complete': {
                id: 'java-complete',
                title: 'Java: The Complete Reference',
                author: 'Herbert Schildt',
                price: 49.99,
                image: '../img/books/Java Complete.jpg'
            },
            'c-complete': {
                id: 'c-complete',
                title: 'C: The Complete Reference',
                author: 'Herbert Schildt',
                price: 45.99,
                image: '../img/books/C,_The_Complete_Reference.jpg'
            },
            'cpp-complete': {
                id: 'cpp-complete',
                title: 'C++: The Complete Reference',
                author: 'Herbert Schildt',
                price: 47.99,
                image: '../img/books/c++ complete.jpg'
            },
            'java-cookbook': {
                id: 'java-cookbook',
                title: 'Java Cookbook',
                author: 'Herbert Schildt',
                price: 42.99,
                image: '../img/books/java cookbook.jpg'
            },
            'java2': {
                id: 'java2',
                title: 'Java 2: The Complete Reference',
                author: 'Herbert Schildt',
                price: 44.99,
                image: '../img/books/Java2.jpg'
            }
        };
        
        this.init();
    }
    
    init() {
        this.renderCart();
        this.updateCartCount();
        this.bindEvents();
        this.addSampleItems(); // Add some sample items for demonstration
    }
    
    // Add sample items for demonstration
    addSampleItems() {
        if (this.items.length === 0) {
            this.addItem('java-complete', 1);
            this.addItem('c-complete', 1);
        }
    }
    
    // Load cart from localStorage
    loadCartFromStorage() {
        try {
            const saved = localStorage.getItem('herbertSchildtCart');
            return saved ? JSON.parse(saved) : [];
        } catch (error) {
            console.error('Error loading cart from storage:', error);
            return [];
        }
    }
    
    // Save cart to localStorage
    saveCartToStorage() {
        try {
            localStorage.setItem('herbertSchildtCart', JSON.stringify(this.items));
        } catch (error) {
            console.error('Error saving cart to storage:', error);
        }
    }
    
    // Add item to cart
    addItem(bookId, quantity = 1) {
        const book = this.bookData[bookId];
        if (!book) {
            console.error('Book not found:', bookId);
            return;
        }
        
        const existingItem = this.items.find(item => item.id === bookId);
        
        if (existingItem) {
            existingItem.quantity += quantity;
        } else {
            this.items.push({
                id: bookId,
                title: book.title,
                author: book.author,
                price: book.price,
                image: book.image,
                quantity: quantity
            });
        }
        
        this.saveCartToStorage();
        this.renderCart();
        this.updateCartCount();
        this.showNotification(`${book.title} added to cart!`);
    }
    
    // Remove item from cart
    removeItem(bookId) {
        this.items = this.items.filter(item => item.id !== bookId);
        this.saveCartToStorage();
        this.renderCart();
        this.updateCartCount();
        this.showNotification('Item removed from cart');
    }
    
    // Update item quantity
    updateQuantity(bookId, newQuantity) {
        if (newQuantity <= 0) {
            this.removeItem(bookId);
            return;
        }
        
        const item = this.items.find(item => item.id === bookId);
        if (item) {
            item.quantity = newQuantity;
            this.saveCartToStorage();
            this.renderCart();
            this.updateCartCount();
        }
    }
    
    // Clear entire cart
    clearCart() {
        if (this.items.length === 0) return;
        
        if (confirm('Are you sure you want to clear all items from your cart?')) {
            this.items = [];
            this.saveCartToStorage();
            this.renderCart();
            this.updateCartCount();
            this.showNotification('Cart cleared');
        }
    }
    
    // Calculate subtotal
    calculateSubtotal() {
        return this.items.reduce((total, item) => total + (item.price * item.quantity), 0);
    }
    
    // Calculate tax
    calculateTax() {
        return this.calculateSubtotal() * this.taxRate;
    }
    
    // Calculate total
    calculateTotal() {
        const subtotal = this.calculateSubtotal();
        const tax = this.calculateTax();
        return subtotal + tax + this.shippingCost;
    }
    
    // Update cart count in header
    updateCartCount() {
        const count = this.items.reduce((total, item) => total + item.quantity, 0);
        const cartCountElement = document.getElementById('cart-count');
        if (cartCountElement) {
            cartCountElement.textContent = count;
        }
    }
    
    // Render cart items
    renderCart() {
        const cartItemsContainer = document.getElementById('cart-items');
        if (!cartItemsContainer) return;
        
        if (this.items.length === 0) {
            cartItemsContainer.innerHTML = `
                <div class="empty-cart">
                    <div class="empty-cart-content">
                        <img src="../img/books/Java Complete.jpg" alt="Empty Cart" class="empty-cart-image">
                        <h3>Your cart is empty</h3>
                        <p>Add some books to get started!</p>
                        <a href="../browse-book.html" class="browse-btn">Browse Books</a>
                    </div>
                </div>
            `;
        } else {
            cartItemsContainer.innerHTML = this.items.map(item => `
                <div class="cart-item" data-book-id="${item.id}">
                    <img src="${item.image}" alt="${item.title}" class="cart-item-image">
                    <div class="cart-item-details">
                        <h3 class="cart-item-title">${item.title}</h3>
                        <p class="cart-item-author">by ${item.author}</p>
                        <p class="cart-item-price">$${item.price.toFixed(2)}</p>
                    </div>
                    <div class="cart-item-controls">
                        <div class="quantity-controls">
                            <button class="quantity-btn" onclick="cart.updateQuantity('${item.id}', ${item.quantity - 1})">-</button>
                            <input type="number" class="quantity-input" value="${item.quantity}" min="1" 
                                   onchange="cart.updateQuantity('${item.id}', parseInt(this.value))">
                            <button class="quantity-btn" onclick="cart.updateQuantity('${item.id}', ${item.quantity + 1})">+</button>
                        </div>
                        <button class="remove-btn" onclick="cart.removeItem('${item.id}')">Remove</button>
                    </div>
                </div>
            `).join('');
        }
        
        this.updateSummary();
    }
    
    // Update order summary
updateSummary() {
    const subtotal = this.calculateSubtotal();

    // Update subtotal element
    document.getElementById('subtotal').textContent = `$${subtotal.toFixed(2)}`;

    // Enable/disable checkout button
    const checkoutBtn = document.getElementById('checkout-btn');
    if (checkoutBtn) {
        checkoutBtn.disabled = this.items.length === 0;
    }
}

    // Show notification
    showNotification(message) {
        // Create notification element
        const notification = document.createElement('div');
        notification.className = 'notification';
        notification.textContent = message;
        notification.style.cssText = `
            position: fixed;
            top: 20px;
            right: 20px;
            background-color: var(--button-primary);
            color: white;
            padding: 1rem 2rem;
            border-radius: 5px;
            font-family: 'Inter Medium';
            z-index: 1000;
            animation: slideInRight 0.3s ease;
        `;
        
        document.body.appendChild(notification);
        
        // Remove notification after 3 seconds
        setTimeout(() => {
            notification.style.animation = 'slideOutRight 0.3s ease';
            setTimeout(() => {
                if (notification.parentNode) {
                    notification.parentNode.removeChild(notification);
                }
            }, 300);
        }, 3000);
    }
    
    // Bind event listeners
    bindEvents() {
        // Clear cart button
        const clearCartBtn = document.getElementById('clear-cart');
        if (clearCartBtn) {
            clearCartBtn.addEventListener('click', () => this.clearCart());
        }
        
        // Checkout button
        const checkoutBtn = document.getElementById('checkout-btn');
        if (checkoutBtn) {
            checkoutBtn.addEventListener('click', () => this.proceedToCheckout());
        }
        
        // Add to cart buttons in recommended section
        document.addEventListener('click', (e) => {
            if (e.target.classList.contains('add-to-cart-btn')) {
                const bookId = e.target.getAttribute('data-book-id');
                this.addItem(bookId, 1);
            }
        });
    }
    
    // Proceed to checkout (placeholder)
    proceedToCheckout() {
        if (this.items.length === 0) return;
        
        // In a real application, this would redirect to a checkout page
        // For now, we'll show an alert
        alert(`Proceeding to checkout with ${this.items.length} item(s). Total: $${this.calculateTotal().toFixed(2)}`);
        
        // You can integrate this with your groupmate's checkout system
        console.log('Checkout data:', {
            items: this.items,
            subtotal: this.calculateSubtotal(),
            tax: this.calculateTax(),
            shipping: this.shippingCost,
            total: this.calculateTotal()
        });
    }
    
    // Get cart data for integration with other pages
    getCartData() {
        return {
            items: this.items,
            subtotal: this.calculateSubtotal(),
            tax: this.calculateTax(),
            shipping: this.shippingCost,
            total: this.calculateTotal(),
            itemCount: this.items.reduce((total, item) => total + item.quantity, 0)
        };
    }
}

// Initialize cart when DOM is loaded
document.addEventListener('DOMContentLoaded', () => {
    window.cart = new ShoppingCart();
});

// Add CSS animations for notifications
const style = document.createElement('style');
style.textContent = `
    @keyframes slideInRight {
        from {
            transform: translateX(100%);
            opacity: 0;
        }
        to {
            transform: translateX(0);
            opacity: 1;
        }
    }
    
    @keyframes slideOutRight {
        from {
            transform: translateX(0);
            opacity: 1;
        }
        to {
            transform: translateX(100%);
            opacity: 0;
        }
    }
`;
document.head.appendChild(style);

// Export for use in other pages
if (typeof module !== 'undefined' && module.exports) {
    module.exports = ShoppingCart;
}
