+@media (min-width: 1200px) {
+    .login-container {
+        padding: 2rem 2vw;
+    }
+    
+    .login-card {
+        width: 40vw;
+        max-width: 500px;
+    }
+}
+
+/* Desktop and larger screens - Scalable approach */
+@media (min-width: 1025px) and (max-width: 1199px) {
+    .login-container {
+        padding: 1.5rem 1.5vw;
+    }
+    
+    .login-card {
+        max-width: 500px;
+    }
