+        font-size: 2.1rem;
+    }
+
+    .login-subtitle {
+        font-size: 1.05rem;
+    }
+
+    .form-group label {
+        font-size: 1.1rem;
+    }
+
+    .form-group input {
+        padding: 0.7rem;
+        font-size: 0.95rem;
+    }
+
+    .login-btn {
+        padding: 0.9rem 1.8rem;
+        font-size: 1.05rem;
+    }
+}
+
+/* Small screens (mobile phones) */
+@media (max-width: 768px) {
+    .login-container {
+        padding: 1rem 5vw;
+        min-height: calc(100vh - 120px);
+    }
+
+    .login-card {
+        padding: 2rem;
+        max-width: 450px;
+    }
+
+    .login-title {
+        font-size: 2rem;
+    }
+
+    .login-subtitle {
+        font-size: 1rem;
+        margin-bottom: 1.5rem;
+    }
+
+    .login-form {
