/* Login Page Styles */
.login-container {
    display: flex;
    justify-content: center;
    align-items: center;
    min-height: calc(100vh - 200px);
    padding: 2rem;
}

.login-card {
    background: #695959;
    color: white;
    padding: 2.5rem 3rem 3rem 3rem;
    border-radius: 12px;
    box-shadow: 0 10px 30px rgba(0,0,0,0.3);
    width: 100%;
    max-width: 550px;
}

.login-title {
    font-size: 2.2rem;
    font-weight: 500;
    text-align: center;
    margin-bottom: 0.5rem;
    color: white;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.3);
}

.login-subtitle {
    text-align: center;
    color: rgba(255,255,255,0.8);
    margin-bottom: 2rem;
    font-size: 1.1rem;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.3);
}

.login-form {
    display: flex;
    flex-direction: column;
    gap: 1.5rem;
}

.form-group {
    display: flex;
    flex-direction: column;
    gap: 0.5rem;
}

.form-group label {
    font-weight: 500;
    color: white;
    font-size: 1.2rem;
    text-shadow: 2px 2px 4px rgba(0,0,0,0.5);
}

.form-group input {
    padding: 0.75rem;
    border: none;
    border-radius: 8px;
    font-size: 1rem;
    background: white;
    color: #333;
    transition: box-shadow 0.3s;
}

.form-group input:focus {
    outline: none;
    box-shadow: 0 0 0 3px rgba(139, 69, 19, 0.3);
}

.form-options {
    display: flex;
    justify-content: space-between;
    align-items: center;
    margin: 1rem 0;
}

.checkbox-label {
    display: flex;
    align-items: center;
    gap: 0.5rem;
    cursor: pointer;
    color: white;
    font-size: 0.9rem;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.3);
}

.checkbox-label input[type="checkbox"] {
    width: 18px;
    height: 18px;
    margin: 0;
}

.forgot-link {
    color: #B3B3E8;
    text-decoration: none;
    font-size: 0.9rem;
    transition: color 0.3s;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.3);
}

.forgot-link:hover {
    color: #B3B3E8;
    text-decoration: underline;
}

.login-btn {
    background: #000000;
    color: white;
    border: 4px solid #000000;
    padding: 1rem 2rem;
    border-radius: 8px;
    font-size: 1.1rem;
    font-weight: 900;
    cursor: pointer;
    transition: all 0.3s;
    margin-top: 1rem;
}

.login-btn:hover {
    background: #333333;
}

.signup-link {
    text-align: center;
    margin-top: 2rem;
    padding-top: 2rem;
    border-top: 1px solid rgba(255,255,255,0.2);
}

.signup-link p {
    color: rgba(255,255,255,0.8);
    margin: 0;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.3);
}

.signup-link a {
    color: white;
    text-decoration: none;
    font-weight: 600;
    transition: color 0.3s;
    text-shadow: 1px 1px 2px rgba(0,0,0,0.3);
}

.signup-link a:hover {
    color: white;
    text-decoration: underline;
}

    .loading-overlay {
    position: fixed;
    top: 0;
    left: 0;
    width: 100%;
    height: 100%;
    background: rgba(0, 0, 0, 0.8);
    display: none; /* hidden first */
    justify-content: center;
    align-items: center;
    z-index: 9999;
  }

  .spinner {
    width: 60px;
    height: 60px;
    border: 6px solid #ffffff33;
    border-top-color: red;
    border-radius: 50%;
    animation: spin 1s linear infinite;
  }

  @keyframes spin {
    to {
      transform: rotate(360deg);
    }
  }



/* Responsive Design */
@media (max-width: 768px) {
    .login-container {
        padding: 1rem;
    }
    
    .login-card {
        padding: 2rem;
    }
    
    .login-title {
        font-size: 2rem;
    }
    
    .form-options {
        flex-direction: column;
        gap: 1rem;
        align-items: flex-start;
    }
}

@media (max-width: 480px) {
    .login-card {
        padding: 1.5rem;
    }
    
    .login-title {
        font-size: 1.8rem;
    }
    
    .login-subtitle {
        font-size: 1rem;
    }
}
