            function loader(seconds, callback) {
                const overlay = document.getElementById('loadingOverlay');
                overlay.style.display = "flex"; // show loader

                setTimeout(() => {
                    overlay.style.display = "none"; // hide again
                    if (callback) callback(); // optional redirect or next action
                }, seconds * 1000);
            }

            // Attach loader to login link
            document.getElementById('userLink').addEventListener('click', function (e) {
                e.preventDefault(); // stop immediate navigation
                loader(2, () => {
                    window.location.href = this.href; // go to login page after loader
                });
            });
