document.addEventListener('DOMContentLoaded', function() {
    const otpForm = document.getElementById('otpForm');
    const otpInputs = document.querySelectorAll('.otp-input');
    const nextBtn = document.getElementById('nextBtn');
    const resendBtn = document.getElementById('resendBtn');
    const otpSubtitle = document.getElementById('otpSubtitle');

    // Get user + selected option
    const users = JSON.parse(localStorage.getItem("users")) || [];
    const emailOTP = localStorage.getItem("emailOTP");
    const selectedType = localStorage.getItem("otpType"); // "email" or "phone"
    const userPIN = JSON.parse(localStorage.getItem("userPIN"));

    const user = users.find(u => u.email === emailOTP);

    if (user) {
        if (selectedType === "email") {
            const maskedEmail = user.email.replace(/(.{2})(.*)(@.*)/,
                (m, p1, p2, p3) => p1 + "*".repeat(p2.length) + p3
            );
            otpSubtitle.textContent = `We have sent the code to your email: ${maskedEmail}`;
        } else {
            const maskedPhone = "â€¢â€¢â€¢â€¢" + user.contactNum.slice(-4);
            otpSubtitle.textContent = `We have sent the code to your phone: ${maskedPhone}`;
        }
    }

    // OTP input handling
    otpInputs[0].focus();

    otpInputs.forEach((input, index) => {
        input.addEventListener('input', function(e) {
            if (!/^\d$/.test(e.target.value)) {
                e.target.value = '';
                return;
            }
            if (index < otpInputs.length - 1) {
                otpInputs[index + 1].focus();
            }
            checkOTPComplete();
        });

        input.addEventListener('keydown', function(e) {
            if (e.key === 'Backspace' && !input.value && index > 0) {
                otpInputs[index - 1].focus();
            }
        });
    });

    function checkOTPComplete() {
        const isComplete = Array.from(otpInputs).every(input => input.value);
        nextBtn.disabled = !isComplete;
    }

    otpForm.addEventListener('submit', function(e) {
        e.preventDefault();
        const enteredOTP = Array.from(otpInputs).map(input => input.value).join('');

        if (!userPIN || enteredOTP !== userPIN.pin.toString()) {
            showError("Invalid OTP code. Please try again.");
            otpInputs.forEach(input => input.value = '');
            otpInputs[0].focus();
            nextBtn.disabled = true;
            return;
        }

        showSuccess("OTP verified successfully!");
        setTimeout(() => {
            window.location.href = 'new-password.html';
        }, 1500);
    });

    // Resend OTP handler (generate new pin & update localStorage)
    resendBtn.addEventListener('click', function() {
        const newPin = Math.floor(10000 + Math.random() * 90000);
        localStorage.setItem("userPIN", JSON.stringify({
            email: user.email,
            pin: newPin
        }));

        showSuccess("A new OTP has been sent.");
    });

    // Notification helpers
    function showError(msg) { showNotification(msg, "error"); }
    function showSuccess(msg) { showNotification(msg, "success"); }
    function showNotification(message, type = 'info') {
        const notification = document.createElement('div');
        notification.className = `notification notification-${type}`;
        notification.textContent = message;
        document.body.appendChild(notification);
        setTimeout(() => notification.remove(), 3000);
    }
});
