commits.content:
@font-face {
  font-family: Encode;
  src: url("../font/Encode_Sans/static/EncodeSans-Regular.ttf") format("truetype");
  font-weight: 400;
  font-style: normal;
  font-display: swap;
}

@font-face {
  font-family: Encode;
  src: url("../font/Encode_Sans/static/EncodeSans-SemiBold.ttf") format("truetype");
  font-weight: 600;
  font-style: normal;
  font-display: swap;
}

:root {
  --brand: #7c5856;
  --brand-600: #6f4c4a;
  --text: #2b2b2b;
  --muted: #676767;
  --bg: #ffffff;
  --border: #d6d1d1;
  --shadow: 0 10px 18px rgba(0, 0, 0, .08);
}

* {
  box-sizing: border-box;
}

html,
body {
  height: 100%;
}

body {
  margin: 0;
  font-family: Encode, system-ui, -apple-system, Segoe UI, Roboto, Ubuntu, Cantarell, Noto Sans, sans-serif;
  color: var(--text);
  background: var(--bg);
  display: flex;
}

.sidebar {
  width: 220px;
  min-width: 220px;
  background: var(--brand);
  color: #fff;
  display: flex;
  flex-direction: column;
  position: sticky;
  top: 0;
  height: 100vh;
  padding: 18px 16px;
  gap: 18px;
}

.brand {
  display: flex;
  align-items: center;
  justify-content: center;
  padding: 6px 0 10px;
}

.brand-box {
  background: #825E5E;
  padding: 10px 12px;
  border-radius: 10px;
  display: inline-flex;
  align-items: center;
  justify-content: center;
}

.brand-logo {
  height: 40px;
  filter: brightness(100%);
}

.burger {
  display: none;
  position: absolute;
  top: 16px;
  right: -48px;
  background: #fff;
  border: none;
  border-radius: 8px;
  padding: 10px;
  box-shadow: var(--shadow);
  cursor: pointer;
}

.burger span {
  display: block;
  width: 22px;
  height: 2px;
  background: var(--brand);
  margin: 4px 0;
}

.menu {
  display: flex;
  flex-direction: column;
  gap: 10px;
}

.menu-item {
  background: #fff1;
  color: #fff;
  border: 1px solid #ffffff33;
  border-radius: 10px;
  padding: 12px 14px;
  text-align: left;
  font-weight: 600;
  cursor: pointer;
}

.img

.menu-item.active,
.menu-item:hover {
  background: #fff;
  border-color: #fff;
  color: var(--brand);
}

.admin-profile {
  margin-top: auto;
  display: flex;
  flex-direction: column;
  align-items: center;
  gap: 8px;
}

.avatar {
  width: 76px;
  height: 76px;
  border-radius: 50%;
  background: #fff1;
  border: 2px solid #ffffff66;
}

.admin-name {
  font-weight: 600;
}

.logout {
  color: #fff;
  background: #fff1;
  border: 1px solid #ffffff33;
  border-radius: 10px;
  padding: 8px 12px;
  text-decoration: none;
}

.logout:hover {
  background: #fff;
  color: var(--brand);
}

.content {
  flex: 1;
  padding: 32px 26px;
}

.section {
  display: none;
}

.section.visible {
  display: block;
}

.section h1 {
  margin: 0 0 18px;
  font-size: 42px;
}

.cards {
  display: grid;
  grid-template-columns: repeat(4, minmax(170px, 1fr));
  gap: 24px;
}

.card {
  background: var(--brand);
  color: #fff;
  border-radius: 14px;
  padding: 28px;
  text-align: center;
  box-shadow: var(--shadow);
}

.card-num {
  font-size: 64px;
  font-weight: 700;
  line-height: 1;
}

.card-label {
  opacity: .9;
  margin-top: 10px;
  font-size: 18px;
}

.section-head {
  display: flex;
  align-items: center;
  justify-content: space-between;
  gap: 16px;
}

.primary,
.secondary,
.danger {
  border: none;
  border-radius: 10px;
  padding: 12px 18px;
  font-weight: 700;
  cursor: pointer;
}

.primary {
  background: var(--brand);
  color: #fff;
}

.primary:hover {
  background: var(--brand-600);
}

.secondary {
  background: #e9e4e4;
  color: var(--text);
}

.danger {
  background: #e02828;
  color: #fff;
}

.table-wrap {
  background: #fff;
  border: 1px solid var(--border);
  border-radius: 10px;
  overflow: auto;
}

.table {
  width: 100%;
  border-collapse: collapse;
  min-width: 740px;
}

.table th,
.table td {
  padding: 16px 18px;
  border-bottom: 1px solid var(--border);
}

.table thead th {
  background: #f6f3f3;
  text-align: left;
}

.table td .small {
  font-size: 12px;
  color: var(--muted);
}

.table button {
  padding: 8px 12px;
  border-radius: 8px;
  border: 1px solid var(--border);
  background: #eee;
  cursor: pointer;
}

.table button:hover {
  background: #e2dfdf;
}

/* MODAL */
.modal {
  position: fixed;
  inset: 0;
  background: rgba(0, 0, 0, .35);
  display: none;
  align-items: center;
  justify-content: center;
  padding: 20px;
}

.modal.show {
  display: flex;
}

.modal-card {
  width: min(980px, 100%);
  background: #fff;
  border-radius: 12px;
  box-shadow: var(--shadow);
  overflow: hidden;
}

.modal-head {
  display: flex;
  align-items: center;
  justify-content: space-between;
  padding: 18px 20px;
  border-bottom: 1px solid var(--border);
}

.modal-body {
  padding: 20px;
}

.icon-close {
  width: 32px;
  height: 32px;
  border-radius: 8px;
  border: 1px solid var(--border);
  background: #f5f5f5;
  cursor: pointer;
}

.icon-close:before {
  content: "\00D7";
  display: block;
  text-align: center;
  font-size: 20px;
  line-height: 30px;
  color: var(--text);
}

.add-book-grid {
  display: grid;
  grid-template-columns: 280px 1fr;
  gap: 24px;
}

.book-image img {
  width: 100%;
  height: auto;
  border: 1px solid var(--border);
  border-radius: 8px;
}

.file-btn {
  display: inline-block;
  text-align: center;
  margin-top: 10px;
}

.book-form .field {
  display: flex;
  flex-direction: column;
  gap: 8px;
  margin-bottom: 12px;
}

.book-form input,
.book-form textarea,
.book-form select {
  padding: 12px 12px;
  border: 1px solid var(--border);
  border-radius: 10px;
  font: inherit;
}

.grid-3 {
  display: grid;
  grid-template-columns: repeat(3, 1fr);
  gap: 12px;
}

.modal-actions {
  display: flex;
  justify-content: flex-end;
  gap: 12px;
  margin-top: 8px;
}

.modal-actions.gap {
  justify-content: flex-start;
}

.user-info .line {
  display: flex;
  gap: 8px;
  margin: 6px 0;
  color: var(--muted);
}

.user-info h3 {
  margin-top: 12px;
}

.chips {
  display: flex;
  flex-wrap: wrap;
  gap: 10px;
  margin-top: 6px;
}

.chips:empty:before {
  content: "No purchase yet";
  color: var(--muted);
}

.chips .chip {
  background: #f0eeee;
  border: 1px solid var(--border);
  border-radius: 999px;
  padding: 6px 10px;
  font-size: 14px;
}

/* RESPONSIVE */
@media (max-width: 1024px) {
  .cards { grid-template-columns: repeat(2, 1fr); }
}

@media (max-width: 840px) {
  .sidebar {
    position: fixed;
    left: 0;
    z-index: 20;
    transform: translateX(-100%);
    transition: transform .25s;
  }
  .sidebar.open { transform: none; }
  .burger { display: block; }
  .content { padding: 24px 16px; margin-left: 0; }
  .add-book-grid { grid-template-columns: 1fr; }
  .grid-3 { grid-template-columns: 1fr; }
}
