/* Minimal, readable JS for the Admin dashboard. Uses localStorage for demo data. */
(function(){
  "use strict";

  const $ = (sel, root=document) => root.querySelector(sel);
  const $$ = (sel, root=document) => Array.from(root.querySelectorAll(sel));

  const SECTIONS = {
    dashboard: $('#dashboard'),
    books: $('#books'),
    users: $('#users')
  };

  const STORAGE_KEYS = {
    books: 'hs_books',
    users: 'hs_users',
    orders: 'hs_orders'
  };

  function getData(key, fallback){
    try{ return JSON.parse(localStorage.getItem(key)) ?? fallback; }catch{ return fallback; }
  }
  function setData(key, value){ localStorage.setItem(key, JSON.stringify(value)); }

  // Seed demo data if empty
  async function seed() {
    const seeded = getData('hs_seeded', false);
    if (seeded) return;

    try {
        // Fetch all books from backend
        const response = await fetch("http://127.0.0.1:5000/get-books");
        const books = await response.json();

        if (!books || books.length === 0) {
            console.warn("No books fetched from database, using demo data.");
            return;
        }

        // Optionally, you can still seed demo users or orders
        const demoUsers = [
            {id: crypto.randomUUID(), name:'Demo User', email:'demo@example.com', purchases:[], joined:'10/11/2025', blocked:false},
        ];
        const demoOrders = 15;

        // Save to localStorage
        setData(STORAGE_KEYS.books, books);
        setData(STORAGE_KEYS.users, demoUsers);
        setData(STORAGE_KEYS.orders, demoOrders);
        setData('hs_seeded', true);

        console.log("[DEBUG] Books seeded from database:", books);

    } catch (err) {
        console.error("Failed to fetch books from backend:", err);
    }
}

document.addEventListener("DOMContentLoaded", seed);

  function switchSection(target){
    Object.values(SECTIONS).forEach(s => s.classList.remove('visible'));
    SECTIONS[target].classList.add('visible');
    $$('.menu-item').forEach(b => b.classList.toggle('active', b.dataset.target===target));
  }

  // Dashboard counters
  function refreshStats(){
    const books = getData(STORAGE_KEYS.books, []);
    const total = books.length;
    const pdf = books.filter(b => b.type==='PDF').length;
    const physical = books.filter(b => b.type==='PHYSICAL').length;
    const orders = getData(STORAGE_KEYS.orders, 0);
    $('#stat-total-books').textContent = total;
    $('#stat-pdf').textContent = pdf;
    $('#stat-physical').textContent = physical;
    $('#stat-orders').textContent = orders;
  }

  // BOOK MANAGEMENT
  function renderBooks(){
    const tbody = $('#books-tbody');
    const books = getData(STORAGE_KEYS.books, []);
    tbody.innerHTML = books.map(b => `
      <tr>
        <td>${b.title}</td>
        <td>${b.category}</td>
        <td>$${Number(b.price).toFixed(2)}</td>
        <td>${b.type}</td>
        <td><button data-action="edit" data-id="${b.id}">EDIT</button></td>
        <td><button data-action="delete" data-id="${b.id}">DELETE</button></td>
      </tr>
    `).join('');
  }

  function openBookModal(editId){
    const modal = $('#book-modal');
    const titleEl = $('#book-modal-title');
    const submitBtn = $('#book-submit');
    const form = $('#book-form');
    const typeSel = $('#book-type');
    const fields = {
      title: $('#book-title'),
      description: $('#book-description'),
      price: $('#book-price'),
      category: $('#book-category'),
      rating: $('#book-rating'),
      year: $('#book-year'),
      pages: $('#book-pages'),
      isbn: $('#book-isbn')
    };
    form.reset();
    form.dataset.editId = editId || '';
    if(editId){
      const book = getData(STORAGE_KEYS.books, []).find(b => b.id===editId);
      if(book){
        titleEl.textContent = 'Edit Book';
        submitBtn.textContent = 'Save Changes';
        fields.title.value = book.title;
        fields.price.value = book.price;
        fields.category.value = book.category;
        typeSel.value = book.type;
      }
    } else {
      titleEl.textContent = 'Add New Books';
      submitBtn.textContent = 'Add New Book';
    }
    modal.classList.add('show');
    modal.setAttribute('aria-hidden', 'false');
  }

  function closeBookModal(){
    const modal = $('#book-modal');
    modal.classList.remove('show');
    modal.setAttribute('aria-hidden', 'true');
  }

  function handleBookSubmit(e){
    e.preventDefault();
    const books = getData(STORAGE_KEYS.books, []);
    const typeSel = $('#book-type');
    const newData = {
      title: $('#book-title').value.trim(),
      category: $('#book-category').value.trim(),
      price: Number($('#book-price').value || 0),
      type: typeSel.value
    };
    const editId = e.currentTarget.dataset.editId;
    if(editId){
      const idx = books.findIndex(b => b.id===editId);
      if(idx>-1) books[idx] = {...books[idx], ...newData};
    }else{
      books.push({id: crypto.randomUUID(), ...newData});
    }
    setData(STORAGE_KEYS.books, books);
    closeBookModal();
    renderBooks();
    refreshStats();
  }

  function onBooksTableClick(e){
    const btn = e.target.closest('button');
    if(!btn) return;
    const id = btn.dataset.id;
    const action = btn.dataset.action;
    if(action==='edit') openBookModal(id);
    if(action==='delete'){
      const books = getData(STORAGE_KEYS.books, []);
      setData(STORAGE_KEYS.books, books.filter(b => b.id!==id));
      renderBooks();
      refreshStats();
    }
  }

  // USERS MANAGEMENT
  function renderUsers(){
    const tbody = $('#users-tbody');
    const users = getData(STORAGE_KEYS.users, []);
    tbody.innerHTML = users.map(u => `
      <tr>
        <td>${u.name}</td>
        <td>${u.email}</td>
        <td>${(u.purchases?.[0]||'').slice(0,16) || ''}</td>
        <td>${u.joined || ''}</td>
        <td><button data-action="view" data-id="${u.id}">View</button></td>
      </tr>
    `).join('');
  }

  function openUserModal(id){
    const users = getData(STORAGE_KEYS.users, []);
    const user = users.find(u => u.id===id);
    if(!user) return;
    $('#u-fullname').textContent = user.name;
    $('#u-email').textContent = user.email;
    $('#u-joined').textContent = user.joined || '';
    const chips = $('#u-books');
    chips.innerHTML = (user.purchases||[]).map(t => `<span class="chip">${t}</span>`).join('');
    $('#user-delete').onclick = function(){
      setData(STORAGE_KEYS.users, users.filter(u => u.id!==id));
      closeUserModal();
      renderUsers();
    };
    $('#user-block').onclick = function(){
      const idx = users.findIndex(u => u.id===id);
      if(idx>-1){ users[idx].blocked = !users[idx].blocked; setData(STORAGE_KEYS.users, users); }
      closeUserModal();
      renderUsers();
    };
    $('#user-modal').classList.add('show');
    $('#user-modal').setAttribute('aria-hidden','false');
  }

  function closeUserModal(){
    $('#user-modal').classList.remove('show');
    $('#user-modal').setAttribute('aria-hidden','true');
  }

  function onUsersTableClick(e){
    const btn = e.target.closest('button');
    if(!btn) return;
    if(btn.dataset.action==='view') openUserModal(btn.dataset.id);
  }

  // NAV + responsive sidebar
  function setupNav(){
    $$('.menu-item').forEach(b => b.addEventListener('click', ()=> switchSection(b.dataset.target)));
    $('#btn-open-add-book').addEventListener('click', ()=> openBookModal());
    $('#books-tbody').addEventListener('click', onBooksTableClick);
    $('#users-tbody').addEventListener('click', onUsersTableClick);
    $('#book-modal-close').addEventListener('click', closeBookModal);
    $('#book-cancel').addEventListener('click', closeBookModal);
    $('#book-form').addEventListener('submit', handleBookSubmit);
    $('#user-modal-close').addEventListener('click', closeUserModal);
    $('#burger').addEventListener('click', ()=> $('.sidebar').classList.toggle('open'));
  }

  // init
  seed();
  setupNav();
  renderBooks();
  renderUsers();
  refreshStats();
})();
