     <script>
-        // localStorage.removeItem('users')
+        document.addEventListener("DOMContentLoaded", function() {
+    emailjs.init("-xbRzNSapSs8iD-WK"); // Initialize EmailJS
 
+    const otpInput = document.getElementById("OTP-confirmation");
+    const sendBtn = document.getElementById("send-OTP");
+    const OTP_STORAGE_KEY = "OTPsign";
+    const OTP_DURATION = 5 * 60; // 5 minutes in seconds
+    let timerInterval = null;
+
+    function generateOTP() {
+        return Math.floor(10000 + Math.random() * 90000); // 5-digit OTP
+    }
+
+    function startTimer(duration) {
+        let remaining = duration;
+        sendBtn.disabled = true;
+
+        if (timerInterval) clearInterval(timerInterval);
+        timerInterval = setInterval(() => {
+            const minutes = Math.floor(remaining / 60);
+            const seconds = remaining % 60;
+            sendBtn.textContent = `${minutes}:${seconds < 10 ? "0" : ""}${seconds}`;
+            remaining--;
+
+            if (remaining < 0) {
+                clearInterval(timerInterval);
+                sendBtn.textContent = "Resend OTP";
+                sendBtn.disabled = false;
+            }
+        }, 1000);
+    }
+
+    async function sendOTP() {
+        const email = otpInput.dataset.email;
+        if (!email) {
+            alert("No email found to send OTP.");
+            return;
+        }
+
+        // Remove old OTP if any
+        localStorage.removeItem(OTP_STORAGE_KEY);
+
+        const otp = generateOTP();
+        localStorage.setItem(OTP_STORAGE_KEY, JSON.stringify({ email, pin: otp }));
+        console.log("📨 OTP stored in localStorage:", otp);
+
+        try {
+            await emailjs.send("service_b816d9f", "template_1yu5htt", {
+                to_email: email,
+                to_name: email,
+                otp_code: otp
+            });
+            console.log("✅ OTP sent via email!");
+        } catch (err) {
+            console.error("❌ Failed to send OTP", err);
+            alert("Failed to send OTP. Try again.");
+            return;
+        }
+
+        startTimer(OTP_DURATION);
+    }
+
+    // First click/focus triggers OTP if none exists
+    otpInput.addEventListener("focus", function() {
+        if (!localStorage.getItem(OTP_STORAGE_KEY)) sendOTP();
+    });
+
+    // Click button to resend OTP
+    sendBtn.addEventListener("click", function() {
+        sendOTP();
+    });
+});
+
     </script>
 </body>
 
 </html>
