* {
    margin: 0;
    padding: 0;
    box-sizing: border-box;
}

:root {
    --primary-color-font: #605858
}


@font-face {
    font-family: 'Inter SemiBold';
    src: url(../font/Inter/static/Inter_18pt-SemiBold.ttf);
}

@font-face {
    font-family: 'Inter Black';
    src: url(../font/Inter/static/Inter_18pt-Black.ttf);
}

@font-face {
    font-family: 'Inter Regular';
    src: url(../font/Inter/static/Inter_28pt-Regular.ttf);
}


@font-face {
    font-family: 'Inter Medium';
    src: url(../font/Inter/static/Inter_28pt-Medium.ttf);
}

@font-face {
    font-family: 'Encode Sans';
    src: url(../font/Encode_Sans/EncodeSans-VariableFont_wdth\,wght.ttf);
}

@font-face {
    font-family: 'Inter Light';
    src: url(../font/Inter/static/Inter_18pt-Light.ttf);
}



.container {
    border-bottom: 1px solid #000;
    background: linear-gradient(180deg, #EDE9E9 0%, #878585 100%);
    height: auto;
}


.header {
    display: flex;
    justify-content: space-between;
    padding-bottom: 1rem;
}

.hero-section {
    display: flex;
    align-items: center;
    height: 35vw;
    gap: 1rem;
}

.author-info {
    padding: 3rem;
    margin-top: 3rem;

}

.author-info::before {
    content: "";
    width: 11rem;
    height: 2px;
    background-color: #000000ad;
    position: absolute;
    margin-top: 6rem;
    transform: translate(-58%) rotate(90deg);
}

.author-info h1 {
    font-family: 'Inter SemiBold';
    font-size: 3em;
    color: #000000c2;
}

.p-info {
    width: 80%;
    margin-top: 1rem;
    font-family: 'Encode Sans';
    line-height: 2;
    font-size: 1em;
    letter-spacing: 1px;
}

.p-info::before {
    content: "";
    position: absolute;
    height: 2px;
    background-color: #000000ad;
    width: 43rem;
    left: 2.5%;

}

.author-info p {
    font-family: 'Encode Sans';
    font-size: 1em;
    padding: 0.5rem;
}

.author-photo {
    width: 50%;
    margin-right: 4rem;
}

.author-photo img {
    height: 28rem;
}

.btn-hero {
    display: flex;
    margin: 1rem 0;
    gap: 1rem;
}

.btn-hero button {
    width: 10rem;
    border: 1px solid black;
    height: 3rem;
    font-size: 1rem;
    font-family: 'Inter SemiBold';
    border-radius: 10px;
}

.btn-hero button:nth-child(1) {
    color: #444343;
    background-color: transparent;
    border: 2px solid #9F6767;
    transition: all 0.5s ease-in-out;

}

.btn-hero button:nth-child(1):active,
.btn-hero button:nth-child(2):active {
    scale: 0.9;
}

.btn-hero button:nth-child(2) {
    background-color: transparent;
    border: 2px solid #695959;
    color: #444343;
    width: 12rem;
    transition: all 0.5s ease-in-out;
}

.btn-hero button:nth-child(2):hover {
    background-color: #695959;
    transform: translateY(-15px);
    color: white;
}

.btn-hero button:nth-child(1):hover {
    background-color: #9F6767;
    color: wheat;
    transform: translateY(-15px);

}

/* popular section */

.popular-info {
    display: flex;
    align-items: center;
    justify-content: center;
    margin: 1rem 3rem;
}

.popular-works-section {
    display: flex;
    flex-direction: column;
    align-items: center;
}

.popular-info h2 {
    font-size: 3.5rem;
    font-family: "Inter Regular";
    font-weight: 100;
    text-shadow:
        2px 2px 0px #01080c6b,
        /* first shadow */
        -3px -3px 0px #3d38379a;
    /* second shadow */
}

.popular-info p {
    font-family: 'Inter regular';
    line-height: 1.5;
    letter-spacing: 1px;
}

.popular-p {
    border-left: 1px solid black;
    border-bottom: 1px solid black;
    padding: 3rem;
}

.book-list {
    display: flex;
    justify-content: space-between;
    align-items: center;
    background-color: #D9D9D9;
    width: 94%;
    margin-top: 2rem;
    overflow: hidden;
    height: 20rem;
    border: 1px solid black;
    position: relative;
}

.books {
    display: flex;
    align-items: center;
    gap: 2rem;
    width: 100%;
    justify-content: center;
    position: relative;
    margin-right: 2rem;
}

.book-list .book {
    height: 16rem;
    width: 11rem;
    border: 1px solid black;
    background-repeat: no-repeat;
    background-size: cover;
    background-image: url(../img/books/C\,_The_Complete_Reference.jpg);
    transition: all 1s ease;
    position: absolute;
}

/* Positions */
.left {
    transform: translateX(-250px) scale(0.9);
    opacity: 0.6;
    z-index: 1;
}

.center {
    transform: translateX(0) scale(1.1);
    opacity: 1;
    z-index: 3;
}

.right {
    transform: translateX(250px) scale(0.9);
    opacity: 0.6;
    z-index: 1;
}


.s-arrow {
    position: absolute;
    display: flex;
    left: 85%;
    display: flex;
    animation: arrowmove 1s infinite ease-in-out;
    z-index: 1;
}

.s-arrow img {
    height: 3rem;
}


.book-list h3 {
    position: absolute;
    transform: rotate(90deg);
    left: 90%;
    font-family: 'Inter semibold';
    width: 10rem;
    bottom: -6rem;
    /* start a bit below container */
    color: rgba(255, 255, 255, 0.774);
    font-size: 2rem;
    animation: moveUp 4s linear infinite;
}

.seemore {
    transition: filter .3s ease;
}

.seemore:hover {
    filter: brightness(0.3);
    cursor: pointer;
}

.book-info {
    display: flex;
    width: 100%;
    margin: 2rem 2rem;
    margin-top: 4rem;
    gap: 1rem;
    justify-content: center;
}

.best-selling-section {
    margin: 1rem 2rem;
    height: auto;
}

.best-selling-section h1 {
    margin: 0 2rem;
    color: var(--primary-color-font);
    font-size: 3em;
    font-family: 'Inter Light';
    font-weight: 100;
}

.best-book {
    width: 100%;
    height: 30rem;
    border: 1px solid black;
}

model-viewer {
    width: 100%;
    height: 100%;
    --progress-bar-color: #7dd3fc;
    background: #E1E3E4;
    cursor: grab;
    /* visual hint for drag */
}

.info-best-book {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    width: 100%;
    position: relative;
    gap: 2rem;
    padding: 0 3rem;
    z-index: 2;
}

.info-best-book::before {
    content: '';
    position: absolute;
    border: 2px solid rgba(0, 0, 0, 0.541);
    height: 70rem;
    left: -100%;
    top: -3rem;
    border-right: none;
    border-bottom: none;
    z-index: 0;
}

.info-best-book::after {
    content: '';
    position: absolute;
    border: 0.5px solid rgba(0, 0, 0, 0.541);
    height: 70rem;
    left: -15%;
    top: -126%;
    transform: rotate(90deg);
}


.info-best-book button {
    display: flex;
    align-items: center;
    width: 30rem;
    justify-content: center;
    gap: 0.9rem;
    height: 3rem;
    color: white;
    font-size: 1.5rem;
    border: none;
    outline: none;
    background-color: #695959;
    border: 1px solid black;
    letter-spacing: 1px;
    font-family: 'Inter Semibold';
    transition: all 0.5s ease-in;
    border-radius: 2px;

}

.info-best-book button:hover img {
    transition: all 0.5s ease-in;
    transform: translateX(5px);
}

.info-best-book button:active {
    scale: 0.4;
}


.info-best-book p {
    font-size: 1em;
    line-height: 1.7;
    width: 80%;
    font-family: 'Encode Sans';
    color: #3A3333;
}

.reader-ratings {
    margin-top: 2rem;
    padding-bottom: 8rem;
}


.reader-ratings .r-headers h2 {
    font-family: 'Inter Light';
    font-weight: 100;
    font-size: 2.8rem;
    padding: 1rem;
    padding-right: 2rem;
}

.r-headers {
    display: flex;
    justify-content: flex-end;
    border-bottom: 1px solid black;
    position: relative;

}

.r-headers::before {
    content: '';
    position: absolute;
    height: 2rem;
    left: 25%;
    top: 80%;
}

.card-comment {
    display: flex;
    flex-wrap: wrap;
    justify-content: center;
    align-items: center;
    height: auto;
    width: 100%;
    gap: 2rem;
    height: 20rem;
}

.rating-card {
    display: flex;
    flex-direction: column;
    align-items: center;
    justify-content: space-between;
    padding: 2rem;
    text-align: center;
    height: 70%;
    font-family: 'Inter Regular';
    background-color: #E6D5D5;
    width: 28%;

    border: 1px solid black;
    border-radius: 5px;
}


.rating-card img {
    height: 35px;
}


.footer {
    background-color: #00000063;
}

.footer-right ul {
    list-style: none;
}

.social-icons img {
    width: 20px;
    height: 20px;
}

.social-icons a {
    display: inline-flex;
    justify-content: center;
    align-items: center;
    width: 42px;
    height: 42px;
    border-radius: 50%;
    background: #fff;
}

.footer-content {
    display: flex;
    justify-content: space-between;
    align-items: flex-start;
    flex-wrap: wrap;
}

.footer {
    background-color: #2c2525;
    /* dark gray-brown */
    color: #f2f2f2;
    padding: 1rem 5%;
    padding-top: 4rem;
    font-family: Arial, sans-serif;
}

.footer-left {
    max-width: 40%;
    font-family: 'Inter SemiBold';
    font-size: 2rem;
}

.footer-left h3 {
    margin-bottom: 0.5rem;
}

.footer-left p {
    margin-bottom: 1.5rem;
    font-size: 0.95rem;
    color: #cfcfcf;
}

.social-icons {
    display: flex;
    gap: 1rem;
}


.footer-right li {
    margin-bottom: 0.8rem;
    font-size: 0.95rem;
}

.footer-right h3 {
    margin-bottom: 0.8rem;
}

.footer-right a {
    color: #f2f2f2;
    text-decoration: underline;
}

.footer hr {
    border: 0;
    border-top: 1px solid #8b8b8b;
    margin: 3rem 0 1rem;
}

.footer-bottom {
    display: flex;
    justify-content: center;
    text-align: center;
    font-size: 1.3rem;
    color: #ddd;
}


@keyframes arrowmove {
    0% {
        transform: translateX(0);
    }

    50% {
        transform: translateX(10px);
        /* move 10px to the right */
    }

    100% {
        transform: translateX(0);
        /* back to start */
    }
}


@keyframes moveUp {
    0% {
        bottom: -50px;
        /* below container */
    }

    100% {
        bottom: 120%;
        /* moves past the top, out of view */
    }
}
