      background: rgba(0, 0, 0, 0.4);
      display: flex;
      justify-content: center;
      align-items: center;
      z-index: 1000;
    }

    /* Modal Box */
    .modal {
      background: #fff;
      border-radius: 10px;
      padding: 2rem;
      max-width: 700px;
      width: 90%;
      box-shadow: 0 10px 25px rgba(0, 0, 0, 0.2);
    }

    /* Content Layout */
    .modal-content {
      display: flex;
      gap: 1.5rem;
    }

    /* Book Image */
    .book-image img {
      width: 140px;
      border-radius: 6px;
    }

    /* Book Details */
    .book-details {
      flex: 1;
    }

    .book-details h2 {
      margin-bottom: 0.5rem;
      font-size: 1.4rem;
      color: #600000;
    }

    .price {
      font-weight: bold;
      font-size: 1.2rem;
      margin: 0.3rem 0;
    }

    .rating {
      color: #944;
      margin-bottom: 1rem;
    }

    .description {
      margin: 1rem 0;
      line-height: 1.5;
      color: #444;
    }

    .meta {
      display: flex;
      flex-wrap: wrap;
      gap: 1rem 2rem;
      font-size: 0.9rem;
      margin-bottom: 1.5rem;
    }

    /* Buttons */
    .actions {
      display: flex;
      gap: 1rem;
    }

    .actions button {
      flex: 1;
      padding: 0.8rem 1rem;
      border-radius: 6px;
      border: none;
      font-size: 1rem;
      cursor: pointer;
      transition: background 0.3s;
    }

    button.preview {
      background: #eee;
      color: #333;
    }

    button.preview:hover {
      background: #ddd;
    }

    button.add-cart {
      background: #b33636;
      color: #fff;
    }

    button.add-cart:hover {
      background: #a02020;
    }
